import { Component } from '@angular/core';

@Component({
  selector: 'app-listar-pensamento',
  templateUrl: './listar-pensamento.component.html',
  styleUrls: ['./listar-pensamento.component.css']
})
export class ListarPensamentoComponent {

}
<section class="container">
   <header class="header d-flex">
      <h2 class="ff-inter">Meu Mural</h2>
      <button routerLink="/criarPensamento" class="botao">Adicionar Pensamento</button>
      <!--router link diretiva esse elemento se torna um link e faz a navegação para uma rota especifica -->
   </header>
   <div class="mural">
     <app-pensamento></app-pensamento>
   </div>
</section>


