import { PensamentoService } from './../pensamento.service';
import { Pensamento } from './../pensamento';
import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-pensamento',
  templateUrl: './pensamento.component.html',
  styleUrls: ['./pensamento.component.css']
})
export class PensamentoComponent implements OnInit {

  //para receber informações de fora do componente pai listarpensamentos
  //esse @input essa propriedade pensamento vai receber informações do componente pai
    @Input() pensamento: Pensamento = {//atributo
        id: 0,
        conteudo: 'I love angular',
        autoria: 'nathan souza',
        modelo: 'modelo3',
        favorito:false
    }

    @Input() listaFavoritos:Pensamento[] = []; //nome da propriedade que eu quero que receba info de fora

   constructor(private service: PensamentoService){}

   ngOnInit(): void {

   }

   larguraPensamento(): string {
      if(this.pensamento.conteudo.length >= 256){
        return 'pensamento-g'
      }
        return 'pensamento-p'
   }

   mudarIconeFavorito(): string{
    if(this.pensamento.favorito === false){
      return 'inativo';
    }
      return 'ativo'
   }

   atualizarFavoritos(){
      //para consumir o metodo do service preciso injetar o service no construtor
      this.service.mudarFavorito(this.pensamento).subscribe(() =>{
        this.listaFavoritos.splice(this.listaFavoritos.indexOf(this.pensamento), 1) //remover aquele card de pensamento que foi desfavoritado
      });

   }
}
//splice modifica o array original removendo o pensamento passo o indice do elemento a partir do qual quero remover e a quantidade de elementos
 <div class="pensamento {{ pensamento.modelo }} ff-roboto-mono" [ngClass]="larguraPensamento()">
    <img src="../../../../assets/imagens/{{ pensamento.modelo }}.png" alt="Aspas Coloridas">
    <p class="conteudo">{{ pensamento.conteudo }}</p>
    <p class="autoria"><b>{{ pensamento.autoria }}</b></p>

    <div class="acoes">
      <button class="botao-favorito" (click)="atualizarFavoritos()">
        <img src="../../../../assets/imagens/icone-favorito-{{ mudarIconeFavorito() }}.png"
         alt="Icone favorito {{ mudarIconeFavorito() }}">
      </button>
        <button class="botao-editar">
          <img src="../../../../assets/imagens/icone-editar.png" routerLink="/pensamentos/editarPensamento/{{pensamento.id}}" alt="Icone de editar">
        </button>
        <button class="botao-excluir" routerLink="/pensamentos/excluirPensamento/{{pensamento.id}}">
          <img src="../../../../assets/imagens/icone-excluir.png" alt="Icone de excluir">
        </button>
    </div>
 </div>

